graphql.query:
mutation CreateNewAccount($name: String!) {
  CreateAccount(name: $name) {
    id
    name
    balance
    createdAt
  }
}

query GetAllAccounts($first: Int!, $after: String) {
  accounts(first: $first, after: $after) {
    edges {
      node {
        id
        name
        balance
        createdAt
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

query GetAccountById($id: ID!) {
  account(id: $id) {
    id
    name
    balance
    createdAt
  }
}

mutation CreateDeposit($accountId: ID!, $amount: Int!) {
  CreateDeposit(accountId: $accountId, amount: $amount) {
    id
    type
    account {
      id
      name
      balance
    }
    amount
    balance
    createdAt
  }
}

mutation CreateTransfer($fromAccountId: ID!, $toAccountId: ID!, $amount: Int!) {
  CreateTransfer(fromAccountId: $fromAccountId, toAccountId: $toAccountId, amount: $amount) {
    transfer {
      id
      type
      amount
      balance
      createdAt
      account {
        id
        name
        balance
      }
    }
    deposit {
      id
      type
      amount
      balance
      createdAt
      account {
        id
        name
        balance
      }
    }
  }
}
